enum Flatten<Fut1, Fut2> {
    First {
            f: Fut1,
        },
    Second {
            f: Fut2,
        },
    Empty,
}

enum Map<Fut, F> {
    Incomplete {
            future: Fut,
            f: F,
        },
    Complete,
}

enum TryFlatten<Fut1, Fut2> {
    First {
            f: Fut1,
        },
    Second {
            f: Fut2,
        },
    Empty,
}

enum TryFlattenErr<Fut1, Fut2> {
    First {
            f: Fut1,
        },
    Second {
            f: Fut2,
        },
    Empty,
}

enum MaybeDone<Fut> where Fut: Future {
    Future(Fut),
    Done(Fut::Output),
    Gone,
}

enum TryMaybeDone<Fut> where Fut: TryFuture {
    Future(Fut),
    Done(Fut::Ok),
    Gone,
}

fn join4<Fut1, Fut2, Fut3, Fut4>(Fut1, Fut2, Fut3, Fut4)
-> Join4<Fut1, Fut2, Fut3, Fut4> where Fut1: Future, Fut2: Future,
Fut3: Future, Fut4: Future

fn join5<Fut1, Fut2, Fut3, Fut4, Fut5>(Fut1, Fut2, Fut3, Fut4, Fut5)
-> Join5<Fut1, Fut2, Fut3, Fut4, Fut5> where Fut1: Future, Fut2: Future,
Fut3: Future, Fut4: Future, Fut5: Future

fn concat(Self) -> Concat<Self> where Self: Sized,
Self::Item: Extend<<<Self as Stream>::Item as IntoIterator>::Item> +
IntoIterator + Default

fn try_concat(Self) -> TryConcat<Self> where Self: Sized,
Self::Ok: Extend<<<Self as TryStream>::Ok as IntoIterator>::Item> +
IntoIterator + Default
